const bel = require('bel')
const csjs = require('csjs-inject')

const icon = require('icon')
const svg = require('./svg.json')

module.exports = header

function header () {
  return bel`<header class="${css.header}">
    <div class="${css.logo}" onclick=${home}>
      <img src="./assets/images/logo-1.png" alt="smartcontract.codes">
    </div>
    <nav class="${css.nav}">
      <button class="button ${css.newContract}">
        <span class=${css.icon_new} onclick=${openNew}>
          ${icon('new', svg.new)}
        </span>
      </button>
      <a href="#">
        <span class="${css.avatar}">
          <img src="./assets/images/user-avatar.jpg" alt="User Avatar">
        </span>
      </a>
    </nav>
  </header>`
}
function openNew () {
  window.open('https://ethereum-play.github.io/editor-solidity/')
}
function home () {
  location.href = `${window.location.origin}${window.location.pathname}`
}
const css = csjs`
  .header {
    grid-area: header;
    display: grid;
    grid-template-areas:
      "logo" "nav";
    grid-template-rows: 120px;
    grid-template-columns: 120px auto 1fr;
    align-items: center;
  }
  .logo {
    grid-area: logo;
    grid-row: 1;
    grid-column: 1;
    vertical-align: middle;
    margin-right: 17px;
  }
  .logo:hover {
    cursor: pointer;
  }
  .nav {
    grid-area: nav;
    grid-row: 1;
    grid-column: 3;
    display: grid;
    grid-template-columns: 1fr 60px;
    justify-items: right;
    align-items: center;
  }
  .avatar {
    display: inline-block;
    width: 45px;
    height: 45px;
    border-radius: 100px;
    background-color: #333;
    overflow: hidden;
    margin-left: 15px;
  }
  .newContract {
    width: 45px;
    height: 45px;
    background: none;
    -webkit-transition: background .3s ease-in-out;
    -moz-transition: background .3s ease-in-out;
    -o-transition: background .3s ease-in-out;
    transition: background .3s ease-in-out;
  }
  .newContract:hover {
    background-color: var(--button-default-hover);
  }
  .newContract span {
    display: flex;
    align-items: center;
    justify-content: center;
  }
  .icon_new svg g {
    fill: var(--icon-new-fill);
    transition: fill .3s ease-in-out;
    -webkit-transition: fill .3s ease-in-out;
    -moz-transition: fill .3s ease-in-out;
    -o-transition: fill .3s ease-in-out;
  }

  @media (max-width: 639px) {
    .logo img {
      width: 80px;
    }
  }
`
