const bel = require('bel')
const csjs = require('csjs-inject')
const icon = require('icon')
const svg = require('./svg.json')
let css
module.exports = header

function header () {
  let editorurl = 'https://ethereum-play.github.io/editor-solidity/'
  return bel`
    <header class="${css.header}">
        <div class="${css.logo}" onclick=${() => reload()}>
            <img src="./assets/images/logo.svg" alt="PlayProject">
            <h2>PlayProject</h2>
        </div>
        <nav class="${css.nav}">
            <button class="button ${css.newContarct}">
                <span class=${css.icon_new}
                  onclick=${() => window.open(editorurl)}>
                  ${icon('new', svg.new)}
                </span>
            </button>
            <a href="#">
                <span class="${css.avatar}"><img src="./assets/images/user-avatar.jpg" alt="User Avatar"></span>
            </a>
        </nav>
    </header>
  `
}




// ===== helpers =====

function reload () {
  location.href = `${window.location.origin}${window.location.pathname}`
}

// ===== css =====

css = csjs`
  .header {
    grid-area: header;
    display: grid;
    grid-template-areas:
      "logo" "nav";
    grid-template-rows: 120px;
    grid-template-columns: 10fr 2fr;
    align-items: center;
  }
  .logo {
    grid-area: logo;
    grid-row: 1;
    grid-column: 1;
  }
  .logo img {
    vertical-align: middle;
    margin-right: 17px;
    width: 43px;
  }
  .logo h2 {
    display: inline;
    vertical-align: middle;
  }
  .logo:hover {
    cursor: pointer;
  }
  .nav {
    grid-area: nav;
    grid-row: 1;
    grid-column: 2;
    display: grid;
    grid-template-columns: 1fr 60px;
    justify-items: right;
    align-items: center;
  }
  .avatar {
    display: inline-block;
    width: 45px;
    height: 45px;
    border-radius: 100px;
    background-color: #333;
    overflow: hidden;
    margin-left: 15px;
  }
  .newContarct {
    width: 45px;
    height: 45px;
    background: none;
    transition: all ease-in-out .3s
  }
  .newContarct:hover {
    background-color: var(--button-default-hover);
  }
  .newContarct span {
    display: flex;
    align-items: center;
    justify-content: center;
  }
  .icon_new svg g {
    fill: var(--icon-new-fill);
    transition: all .3s ease-in-out;
  }

  @media (max-width: 639px) {
    .logo h2 {
      font-size: 3rem;
    }
    .logo img {
      width: 35px;
    }
  }
`
