const bel = require('bel')
const csjs = require('csjs-inject')
let css
module.exports = header

function header () {
  return bel`
    <header class="${css.header}">
        <div class="${css.logo}">
            <img src="./images/logo.svg" alt="PlayProject">
            <h2>PlayProject</h2>
        </div>
        <nav class="${css.nav}">
            <button class="${css.buttonDefault} ${css.createNewProject}">
                <span class="icon-new">
                  <object data="./images/icons/icon-new.svg" type="image/svg+xml"></object>
                </span>
            </button>
            <a href="#">
                <span class="${css.avatar}"><img src="./images/user-avatar.jpg" alt="User Avatar"></span>
            </a>
        </nav>
    </header>
  `
}



// ===== css =====

css = csjs`
  .header {
    grid-area: header;
    display: grid;
    grid-template-areas:
      "logo" "nav";
    grid-template-rows: 120px;
    grid-template-columns: 10fr 2fr;
    align-items: center;
  }
  .logo {
    grid-area: logo;
    grid-row: 1;
    grid-column: 1;
  }
  .logo img {
    vertical-align: middle;
    margin-right: 17px;
    width: 43px;
  }
  .logo h2 {
    display: inline;
    vertical-align: middle;
  }
  .nav {
    grid-area: nav;
    grid-row: 1;
    grid-column: 2;
    display: grid;
    grid-template-columns: 1fr 60px;
    justify-items: right;
    align-items: center;
  }
  .avatar {
    display: inline-block;
    width: 45px;
    height: 45px;
    border-radius: 100px;
    background-color: #333;
    overflow: hidden;
    margin-left: 15px;
  }
  .createNewProject {
    width: 45px;
    height: 45px;
  }
  .createNewProject span {
    display: flex;
    align-items: center;
    justify-content: center;
  }
  .buttonDefault {
    background-color: var(--button-primary);
    transition: background ease-in-out .3s
  }
  .buttonDefault:hover {
    background-color: var(--button-primary-hover);
  }

  @media (max-width: 639px) {
    .logo h2 {
      font-size: 3rem;
    }
    .logo img {
      width: 35px;
    }
  }
`