const bel = require('bel')
const csjs = require('csjs-inject')

module.exports = loadingInProgress

function loadingInProgress (width, height) {
  return bel`<div class=${css.loading}></div>`
}

const css = csjs`
  .loading{
    z-index: 1;
    width:100%;
    height:100%;
    display:block;
    position:absolute
  }

  .loading:before,
  .loading:after,
  .loading>div{
    width:100px;
    height:100px;
    top:20%;
    left:48.5%;
    display:block;
    position:absolute;
    border-radius:50%;
    border:4px solid #6700ff
  }

  .loading:before
  {content:"";animation:scale 1s ease-in infinite}

  .loading:after
  {content:"";animation:scale 2s ease-in infinite}

  .loading>div{animation:scale 3s ease-in infinite}

  @keyframes scale{
  from{transform:translate(-50%,-50%) scale(0,0)}
  to{transform:translate(-50%,-50%) scale(1,1)}
  }
`
