const bel = require('bel')
const csjs = require('csjs-inject')

module.exports = progressbar

function progressbar () {
  return bel`<div class=${css.progressbar}></div>`
}

const css = csjs`
  .progressbar {
    padding: 10px;
    background-color: #6700ff;
    font-size: var(--text-large);
    text-align: center;
    margin-bottom: 60px;
    font-weight: 200;
  }
`
