const bel = require('bel')
const csjs = require('csjs-inject')
let css
module.exports = search

function search () {
  return bel`
    <div class=${css.searchBar}>
      <button class=${css.submit}>
        <object data="./images/icons/icon-search.svg" type="image/svg+xml"></object>
      </button>
      <input type="text" value="Search contract" onclick="${(e)=>e.target.select()}">
    </div>
  `
}

// ===== css =====

css = csjs`
  .searchBar {
    position: relative;
    padding-bottom: 50px;
  }
  input[type="text"] {
    height: 40px;
    width: calc(100% - 65px);
    border: var(--search-input);
    border-radius: 6px;
    background: var(--search-input-background);
    text-transform: uppercase;
    font-size: var(--search-input-text);
    padding: 4px 50px 4px 15px;
    color: var(--search-input-color);
    outline: none;
    font-family: 'Nunito', sans-serif;
  }
  .submit {
    position: absolute;
    right: 5px;
    top: 5px;
    background: transparent;
    width: 40px;
    height: 40px;
    cursor: pointer;
  }
  .submit object {
    width: 30px;
    height: 30px;
  }
`
